sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	var dict = {hr:['Payslip','HRA','DA'],it:['Hardware','Software','Network'],legal:['Divorce','Marriage','Land'],facilities:['Hostel','Cab','Night'],finance:['BCOM','MBA','CA']};
	if(newValue){
		dynamicOptions(dict[newValue]);
	}
}

function dynamicOptions(values) {
    g_form.clearOptions('u_what_needed');
    for (var i = 0; i < values.length; i++) {
        g_form.addOption('u_what_needed', values[i].toLowerCase(), values[i]);
    }
    g_form.addOption('u_what_needed', 'other', 'Other');
}
